script:
bh.local.reqParams = {
    state : crypto.randomBytes(16).toString('hex'),
    nonce : crypto.randomBytes(16).toString('hex'),
    isMobile : bh.input.query.isMobile,
    redirectTo : bh.input.query.redirectTo
};

const authorizationRequest = Object.assign({
    redirect_uri: url.resolve(bh.web.req.href, '/api/login/cb'),
    scope: 'openid profile email address phone user',
    state: bh.local.reqParams.state,
    nonce: bh.local.reqParams.nonce,
    response_type: bh.input.client.response_types[0],
}, bh.input.authParams);

bh.local.redirectHeaders = {
    location: bh.input.client.authorizationUrl(authorizationRequest)
};

bh.local.res = {
    message: 'IDS client not registered. Register on the Neutrinos Stuido and try again'
}

const protocol = bh.input.headers['x-forwarded-proto'] || bh.web.req.protocol;
const href = protocol + "://" + bh.web.req.get('Host') + bh.web.req.originalUrl;
bh.web.req.href = href;

const params = bh.input.client.callbackParams(bh.web.req);
let tokenset = await bh.input.client.callback(url.resolve(bh.web.req.href, 'cb'), params, {
    'nonce': bh.input.sessionParams.data.nonce,
    'state': bh.input.sessionParams.data.state
});

bh.local.redirectTo = bh.input.sessionParams.data.redirectTo

bh.local.userDetails = {
    tokenset: Object.assign({}, tokenset),
    userInfo: await bh.input.client.userinfo(tokenset['access_token'])
}
bh.local.userDetails['tokenset']['claims'] = Object.assign({}, tokenset.claims());

bh.local.sessionExists = false;
if (bh.local.sessionData && bh.local.sessionData.data && bh.local.sessionData.data.tokenset) {
    bh.local.sessionData['data']['redirectTo'] = bh.input.query['redirectTo'];
    bh.local.sessionData['data']['isMobile'] = bh.input.query['isMobile'];
    bh.local.sessionExists = true;
} else {
    delete bh.local.sessionData['redirectTo'];
}

await Promise.all([
    bh.local.sessionData.data.tokenset.access_token ? bh.input.client.revoke(bh.local.sessionData.data.tokenset.access_token, 'access_token') : undefined,
    bh.local.sessionData.data.tokenset.refresh_token ? bh.input.client.revoke(bh.local.sessionData.data.tokenset.refresh_token, 'refresh_token') : undefined,
]);

bh.local.res = {
        'idsURL': url.format(Object.assign(url.parse(bh.input.client.issuer.end_session_endpoint), {
            search: null,
            query: {
                id_token_hint: bh.local.sessionData.data.tokenset.id_token,
                post_logout_redirect_uri: url.resolve(bh.web.req.href, '/api/logout/cb'),
                client_id: settings.default['ids']['client_id']
            },
        })),
        'sessionExists': true,
}

// Remove user info and tokenset before login redirect
bh.local.sessionData.data.tokenset = null;
bh.local.sessionData.data.userInfo = null;

bh.local.sessionExists = false;

if (bh.local.sessionData && bh.local.sessionData.data && bh.local.sessionData.data.tokenset &&
    bh.local.sessionData.data.tokenset.access_token && bh.local.sessionData.data.tokenset.refresh_token) {
    bh.local.sessionExists = true;
}
